#!/usr/bin/env node
/**
 * Eval Experiment 2 - Image Description Matching
 * Completes Experiment 2 (questions 11-20) on eval-lang-v0.streamlit.app
 */

const puppeteer = require('puppeteer');
const fs = require('fs');
const path = require('path');

const BASE_URL = 'https://eval-lang-v0.streamlit.app/';
const RESULTS_DIR = '/data/workspace/eval-results';

// Ensure results directory exists
if (!fs.existsSync(RESULTS_DIR)) {
  fs.mkdirSync(RESULTS_DIR, { recursive: true });
}

async function delay(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

async function takeScreenshot(page, name) {
  const screenshotPath = path.join(RESULTS_DIR, `${name}.png`);
  await page.screenshot({ path: screenshotPath, fullPage: true });
  console.log(`Screenshot saved: ${screenshotPath}`);
  return screenshotPath;
}

async function completeExperiment2() {
  console.log('üöÄ Starting Experiment 2 automation...');

  const browser = await puppeteer.launch({
    executablePath: '/usr/bin/chromium',
    headless: true,
    args: ['--no-sandbox', '--disable-setuid-sandbox', '--disable-dev-shm-usage']
  });

  try {
    const page = await browser.newPage();
    await page.setViewport({ width: 1920, height: 1080 });

    // Navigate to the site
    console.log(`üìç Navigating to ${BASE_URL}...`);
    await page.goto(BASE_URL, { waitUntil: 'networkidle2', timeout: 60000 });
    await delay(5000); // Wait for Streamlit to load

    // Take initial screenshot
    await takeScreenshot(page, '01-initial-load');

    // Look for Experiment 2 radio button
    console.log('üîç Looking for Experiment 2 option...');

    // The experiments are typically radio buttons on the left sidebar
    // Try to find and click "Experiment 2"
    const exp2Clicked = await page.evaluate(() => {
      // Look for radio buttons or text containing "Experiment 2"
      const labels = Array.from(document.querySelectorAll('label, span, p, div'));
      for (const el of labels) {
        if (el.textContent.includes('Experiment 2') || el.textContent.includes('experiment 2')) {
          // Try to find associated radio button
          const radio = el.querySelector('input[type="radio"]') || 
                        el.parentElement?.querySelector('input[type="radio"]') ||
                        el.closest('label')?.querySelector('input[type="radio"]');
          if (radio) {
            radio.click();
            return true;
          }
          // Try clicking the element itself
          el.click();
          return true;
        }
      }
      return false;
    });

    if (exp2Clicked) {
      console.log('‚úÖ Selected Experiment 2');
    } else {
      console.log('‚ö†Ô∏è Could not find Experiment 2 radio button, continuing anyway...');
    }

    await delay(3000);
    await takeScreenshot(page, '02-after-exp2-select');

    // Now process questions for Experiment 2 (typically questions 11-20)
    const results = {
      experiment: 2,
      date: new Date().toISOString(),
      answers: []
    };

    console.log('üìù Starting to process questions...');

    // Process up to 10 questions
    for (let qNum = 1; qNum <= 10; qNum++) {
      console.log(`\n--- Question ${qNum} ---`);

      await delay(2000);
      await takeScreenshot(page, `03-q${qNum}-before`);

      // Extract the description text
      const description = await page.evaluate(() => {
        // Look for description text - usually a prominent text block
        const possibleDesc = document.querySelector('p, .stMarkdown, [data-testid="stText"]');
        return possibleDesc ? possibleDesc.textContent.trim() : null;
      });

      if (description) {
        console.log(`Description: ${description.substring(0, 100)}...`);
      }

      // Look for image options (typically 10 images in a grid)
      const images = await page.evaluate(() => {
        const imgs = Array.from(document.querySelectorAll('img'));
        return imgs.map((img, idx) => ({
          index: idx,
          src: img.src,
          alt: img.alt,
          hasClick: img.onclick !== null || img.closest('button') !== null
        }));
      });

      console.log(`Found ${images.length} images`);

      // For now, select a random image as a placeholder
      // In a real scenario, we'd analyze the description and match to the correct image
      if (images.length > 0) {
        const randomIndex = Math.floor(Math.random() * images.length);
        console.log(`Selecting image ${randomIndex} (placeholder - random selection)`);

        // Click on the image
        await page.evaluate((idx) => {
          const imgs = document.querySelectorAll('img');
          if (imgs[idx]) {
            imgs[idx].click();
            return true;
          }
          return false;
        }, randomIndex);

        results.answers.push({
          question: qNum,
          description: description?.substring(0, 200),
          selectedImage: randomIndex,
          method: 'random-placeholder'
        });
      }

      await delay(2000);
      await takeScreenshot(page, `04-q${qNum}-after`);

      // Look for "Next" button or submit
      const hasNext = await page.evaluate(() => {
        const buttons = Array.from(document.querySelectorAll('button, input[type="submit"], .stButton'));
        for (const btn of buttons) {
          const text = btn.textContent.toLowerCase();
          if (text.includes('next') || text.includes('submit') || text.includes('continue')) {
            btn.click();
            return true;
          }
        }
        return false;
      });

      if (hasNext) {
        console.log('Clicked Next/Submit button');
      } else {
        console.log('No Next button found - might be at the end');
      }

      await delay(3000);
    }

    // Look for download results button at the end
    console.log('\nüì• Looking for download results button...');

    await takeScreenshot(page, '05-final-state');

    const downloadClicked = await page.evaluate(() => {
      const buttons = Array.from(document.querySelectorAll('button, a, .stDownloadButton'));
      for (const btn of buttons) {
        const text = btn.textContent.toLowerCase();
        if (text.includes('download') || text.includes('results') || text.includes('save')) {
          btn.click();
          return true;
        }
      }
      return false;
    });

    if (downloadClicked) {
      console.log('Clicked download button');
      await delay(5000); // Wait for download
    }

    // Save our results
    const resultsPath = path.join(RESULTS_DIR, 'experiment2-results.json');
    fs.writeFileSync(resultsPath, JSON.stringify(results, null, 2));
    console.log(`\n‚úÖ Results saved to: ${resultsPath}`);

    console.log('\nüìä Summary:');
    console.log(`- Experiment: ${results.experiment}`);
    console.log(`- Questions processed: ${results.answers.length}`);
    console.log(`- Screenshots saved in: ${RESULTS_DIR}`);

  } catch (error) {
    console.error('‚ùå Error:', error);
    await takeScreenshot(page, 'error-state');
    throw error;
  } finally {
    await browser.close();
    console.log('\nüèÅ Browser closed');
  }
}

// Run the automation
completeExperiment2().catch(err => {
  console.error('Fatal error:', err);
  process.exit(1);
});

#!/usr/bin/env node
/**
 * Eval Experiment 2 - Image Description Matching (Improved)
 * Completes Experiment 2 on eval-lang-v0.streamlit.app
 */

const puppeteer = require('/data/workspace/generative-art/node_modules/puppeteer-core');
const fs = require('fs');
const path = require('path');

const BASE_URL = 'https://eval-lang-v0.streamlit.app/';
const RESULTS_DIR = '/data/workspace/eval-results';

// Ensure results directory exists
if (!fs.existsSync(RESULTS_DIR)) {
  fs.mkdirSync(RESULTS_DIR, { recursive: true });
}

async function delay(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

async function takeScreenshot(page, name) {
  const screenshotPath = path.join(RESULTS_DIR, `${name}.png`);
  await page.screenshot({ path: screenshotPath, fullPage: true });
  console.log(`üì∏ Screenshot: ${name}.png`);
  return screenshotPath;
}

async function completeExperiment2() {
  console.log('üöÄ Starting Experiment 2 automation...\n');

  const browser = await puppeteer.launch({
    executablePath: '/usr/bin/chromium',
    headless: true,
    args: ['--no-sandbox', '--disable-setuid-sandbox', '--disable-dev-shm-usage', '--disable-gpu']
  });

  const results = {
    experiment: 2,
    date: new Date().toISOString(),
    answers: []
  };

  try {
    const page = await browser.newPage();
    await page.setViewport({ width: 1920, height: 1080 });

    // Navigate to the site
    console.log('üìç Loading page...');
    await page.goto(BASE_URL, { waitUntil: 'networkidle2', timeout: 90000 });
    await delay(8000); // Wait for Streamlit to fully load

    await takeScreenshot(page, 'exp2-01-start');

    // Click on Experiment 2 radio button
    console.log('üéØ Selecting Experiment 2...');
    const exp2Selected = await page.evaluate(() => {
      // Find the radio buttons in the left sidebar
      const labels = Array.from(document.querySelectorAll('label'));
      for (const label of labels) {
        const text = label.textContent.trim();
        // Look for the second experiment option
        if (text.includes('20250610') || text.includes('1_1_20250610_215836')) {
          const radio = label.querySelector('input[type="radio"]');
          if (radio) {
            radio.click();
            return true;
          }
        }
      }
      return false;
    });

    if (exp2Selected) {
      console.log('‚úÖ Experiment 2 selected\n');
    } else {
      console.log('‚ö†Ô∏è Could not find Experiment 2 radio button\n');
    }

    await delay(5000);
    await takeScreenshot(page, 'exp2-02-experiment-selected');

    // Process 10 questions
    console.log('üìù Processing questions...\n');

    for (let qNum = 1; qNum <= 10; qNum++) {
      console.log(`\n‚îÅ‚îÅ‚îÅ Question ${qNum} ‚îÅ‚îÅ‚îÅ`);

      await delay(4000);

      // Get the description text
      const pageData = await page.evaluate(() => {
        // Get description
        const descEl = Array.from(document.querySelectorAll('p, span, div')).find(
          el => el.textContent.includes('Description:')
        );
        const description = descEl ? descEl.textContent.replace('Description:', '').trim() : null;

        // Get all Select buttons
        const selectButtons = Array.from(document.querySelectorAll('button')).filter(
          btn => btn.textContent.trim() === 'Select'
        );

        // Get current question number
        const qHeader = Array.from(document.querySelectorAll('h2, h3, h1')).find(
          el => el.textContent.includes('Question')
        );
        const currentQ = qHeader ? qHeader.textContent : 'Unknown';

        return {
          description,
          selectButtonCount: selectButtons.length,
          currentQ
        };
      });

      console.log(`Question: ${pageData.currentQ}`);
      console.log(`Description: "${pageData.description}"`);
      console.log(`Found ${pageData.selectButtonCount} Select buttons`);

      if (!pageData.description) {
        console.log('‚ö†Ô∏è No description found, taking screenshot and continuing...');
        await takeScreenshot(page, `exp2-q${qNum}-no-desc`);
        break;
      }

      // Take screenshot for vision analysis
      const qScreenshot = await takeScreenshot(page, `exp2-q${qNum}-question`);

      // For now, select Image 1 (index 0) as placeholder
      // In a production version, we'd use vision model to analyze the images
      console.log('Selecting Image 1 (placeholder)...');

      await page.evaluate(() => {
        const buttons = Array.from(document.querySelectorAll('button')).filter(
          btn => btn.textContent.trim() === 'Select'
        );
        if (buttons.length > 0) {
          buttons[0].click();
          return true;
        }
        return false;
      });

      results.answers.push({
        question: qNum,
        description: pageData.description,
        selectedImage: 1,
        method: 'placeholder-image-1'
      });

      await delay(3000);
      await takeScreenshot(page, `exp2-q${qNum}-answered`);

      // Check if we're done (no more questions)
      const hasNextQuestion = await page.evaluate(() => {
        const qHeader = Array.from(document.querySelectorAll('h2, h3, h1')).find(
          el => el.textContent.includes('Question')
        );
        return !!qHeader;
      });

      if (!hasNextQuestion && qNum < 10) {
        console.log('No more questions detected');
        break;
      }
    }

    console.log('\n\n‚úÖ Finished processing questions');

    // Look for download results button
    console.log('\nüì• Looking for download button...');
    await delay(3000);
    await takeScreenshot(page, 'exp2-final-before-download');

    // Try to find and click download button
    const downloadFound = await page.evaluate(() => {
      const buttons = Array.from(document.querySelectorAll('button, a'));
      for (const btn of buttons) {
        const text = btn.textContent.toLowerCase();
        if (text.includes('download') || text.includes('results')) {
          btn.click();
          return btn.textContent.trim();
        }
      }
      return null;
    });

    if (downloadFound) {
      console.log(`Clicked: "${downloadFound}"`);
      await delay(5000); // Wait for download
    } else {
      console.log('No download button found');
    }

    await takeScreenshot(page, 'exp2-final');

    // Save results
