    ctx.beginPath();
    ctx.arc(0, 0, 25, Math.PI, 0);
    ctx.bezierCurveTo(25, 15, -25, 15, -25, 0);
    ctx.fill();

    // Tentacles
    ctx.strokeStyle = color + '99';
