        const offsetX = (i - 2.5) * 6;
        ctx.beginPath();
        ctx.moveTo(offsetX, 10);
        for (let t = 0; t < 40; t += 2) {
            const waveX = Math.sin(t * 0.2 + i) * 3;
            ctx.lineTo(offsetX + waveX, 10 + t);
        }
        ctx.stroke();
    }

    ctx.restore();
}

