           print(f\"‚úì Connected to SMTP at {SMTP_HOST}:{SMTP_PORT}\")\n            return True\n        except Exception as e:\n            self.errors.append(f\"SMTP connection failed: {e}\")\n            print(f\"‚úó SMTP connection failed: {e}\")\n            return False\n    \n    def decode_header_value(self, header_value):\n        \"\"\"Decode email header value\"\"\"\n        if not header_value:\n            return \"\"\n        decoded = decode_header(header_value)\n        result = []\n        for part, charset in decoded:\n            if isinstance(part, bytes):\n                try:\n                    result.append(part.decode(charset or 'utf-8'))\n                except:\n                    result.append(part.decode('utf-8', errors='ignore'))\n            else:\n                result.append(part)\n        return \"\".join(result)\n    \n    def get_body(self, msg):\n        \"\"\"Extract email body\"\"\"\n        body = \"\"\n        if msg.is_multipart():\n            for part in msg.walk():\n                content_type = part.get_content_type()\n                if content_type == \"text/plain\":\n                    try:\n                        body = part.get_payload(decode=True).decode('utf-8', errors='ignore')\n                        break\n                    except:\n                        pass\n                elif content_type == \"text/html\" and not body:\n                    try:\n                        import re\n                        html = part.get_payload(decode=True).decode('utf-8', errors='ignore')\n                        # Simple HTML to text\n                        body = re.sub('<[^<]+?>', '', html)\n                    except:\n                        pass\n        else:\n            try:\n                body = msg.get_payload(decode=True).decode('utf-8', errors='ignore')\n            except:\n                pass\n        return body\n    \n    def should_skip_sender(self, from_addr, from_name):\n        \"\"\"Check if sender should be skipped\"\"\"\n        from_lower = (from_addr + \" \" + from_name).lower()\n        \n        # Check automated senders\n        for auto in AUTO_SENDERS:\n            if auto in from_lower:\n                return True, f\"Automated sender: {auto}\"\n        \n        # Check if it's a human we should respond to\n        for human in HUMANS_TO_RESPOND:\n            if human in from_lower:\n                return False, f\"Human sender: {human}\"\n        \n        # Unknown sender - skip to be safe\n        return True, f\"Unknown sender: {from_addr}\"\n    \n    def list_unread(self):\n        \"\"\"List all unread emails\"\"\"\n        try:\n            self.imap.select('inbox')\n            status, messages = self.imap.search(None, 'UNSEEN')\n            if status != 'OK':\n                return []\n            \n            message_ids = messages[0].split()\n            print(f\"Found {len(message_ids)} unread emails\")\n            return message_ids\n        except Exception as e:\n            self.errors.append(f\"Failed to list unread: {e}\")\n            return []\n    \n    def fetch_email(self, msg_id):\n        \"\"\"Fetch a specific email\"\"\"\n        try:\n            status, msg_data = self.imap.fetch(msg_id, '(RFC822)')\n            if status != 'OK':\n                return None\n            \n            raw_email = msg_data[0][1]\n            msg = email.message_from_bytes(raw_email)\n            \n            from_header = self.decode_header_value(msg.get('From', ''))\n            subject = self.decode_header_value(msg.get('Subject', ''))\n            date = msg.get('Date', '')\n            message_id = msg.get('Message-ID', '')\n            \n            # Parse from header\n            import re\n            match = re.match(r'\"?([^\"]+)\"?\\s*<([^>]+)>', from_header)\n            if match:\n                from_name = match.group(1).strip()\n                from_addr = match.group(2).strip()\n            else:\n                from_name = \"\"\n                from_addr = from_header.strip()\n            \n            body = self.get_body(msg)\n            \n            return {\n                'id': msg_id.decode() if isinstance(msg_id, bytes) else msg_id,\n                'from_name': from_name,\n                'from_addr': from_addr,\n                'subject': subject,\n                'date': date,\n                'message_id': message_id,\n                'body': body\n            }\n        except Exception as e:\n            self.errors.append(f\"Failed to fetch email {msg_id}: {e}\")\n            return None\n    \n    def mark_as_read(self, msg_id):\n        \"\"\"Mark email as read\"\"\"\n        try:\n            self.imap.store(msg_id, '+FLAGS', '\\\\Seen')\n            return True\n        except Exception as e:\n            self.errors.append(f\"Failed to mark as read {msg_id}: {e}\")\n            return False\n    \n    def close(self):\n        \"\"\"Close connections\"\"\"\n        if self.imap:\n            try:\n                self.imap.close()\n                self.imap.logout()\n            except:\n                pass\n        if self.smtp:\n            try:\n                self.smtp.quit()\n            except:\n                pass\n\ndef main():\n    # Load credentials from .env.email\n    env_file = '/data/workspace/.env.email'\n    if os.path.exists(env_file):\n        with open(env_file) as f:\n            for line in f:\n                if '=' in line and not line.startswith('#'):\n                    key, val = line.strip().split('=', 1)\n                    os.environ[key] = val\n    \n    # Update config from environment\n    global IMAP_PASSWORD, SMTP_PASSWORD\n    IMAP_[REDACTED] IMAP_PASSWORD)\n    SMTP_[REDACTED] SMTP_PASSWORD)\n    \n    if not IMAP_PASSWORD or not SMTP_[REDACTED]        print(\"ERROR: IMAP_PASSWORD and SMTP_PASSWORD must be set\")\n        sys.exit(1)\n    \n    processor = EmailProcessor()\n    \n    # Connect to IMAP\n    if not processor.connect_imap():\n        sys.exit(1)\n    \n    # List unread emails\n    unread_ids = processor.list_unread()\n    \n    if not unread_ids:\n        print(\"No unread emails to process\")\n        processor.close()\n        sys.exit(0)\n    \n    # Process each email\n    emails_to_reply = []\n    \n    for msg_id in unread_ids:\n        email_data = processor.fetch_email(msg_id)\n        if not email_data:\n            continue\n        \n        print(f\"\\n--- Email {email_data['id']} ---\")\n        print(f\"From: {email_data['from_name']} <{email_data['from_addr']}>\")\n        print(f\"Subject: {email_data['subject']}\")\n        \n        should_skip, reason = processor.should_skip_sender(email_data['from_addr'], email_data['from_name'])\n        \n        if should_skip:\n            print(f\"SKIP: {reason}\")\n            processor.skipped.append({\n                'id': email_data['id'],\n                'from': email_data['from_addr'],\n                'subject': email_data['subject'],\n                'reason': reason\n            })\n            # Mark automated emails as read\n            processor.mark_as_read(msg_id)\n        else:\n            print(f\"REPLY NEEDED: {reason}\")\n            emails_to_reply.append(email_data)\n    \n    processor.close()\n    \n    # Save results for the agent to process\n    result = {\n        'timestamp': datetime.now().isoformat(),\n        'total_unread': len(unread_ids),\n        'skipped': processor.skipped,\n        'to_reply': emails_to_reply,\n        'errors': processor.errors\n    }\n    \n    output_file = '/data/workspace/email_processing_result.json'\n    with open(output_file, 'w') as f:\n        json.dump(result, f, indent=2)\n    \n    print(f\"\\n{'='*50}\")\n    print(f\"Results saved to: {output_file}\")\n    print(f\"Total unread: {len(unread_ids)}\")\n    print(f\"Skipped: {len(processor.skipped)}\")\n    print(f\"Need reply: {len(emails_to_reply)}\")\n    \n    return result\n\nif __name__ == '__main__':\n    main()\n",
