#!/usr/bin/env node
/**
 * Tool Success Tracker
 * Logs tool calls, tracks outcomes, learns patterns
 */

const fs = require('fs');
const path = require('path');

const DB_PATH = process.env.TOOL_TRACKER_DB || '/data/workspace/.tool-tracker/db.jsonl';
const MAX_ENTRIES = parseInt(process.env.TOOL_TRACKER_MAX) || '10000';

// Ensure directory exists
const dbDir = path.dirname(DB_PATH);
if (!fs.existsSync(dbDir)) {
  fs.mkdirSync(dbDir, { recursive: true });
}

/**
 * Log a tool call
 */
function logToolCall(record) {
  const entry = {
    id: `call-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
    timestamp: new Date().toISOString(),
    ...record
  };

  fs.appendFileSync(DB_PATH, JSON.stringify(entry) + '\n');

  // Rotate if too large
  rotateIfNeeded();

  return entry.id;
}

/**
 * Rotate DB if it gets too large
 */
function rotateIfNeeded() {
  try {
    const lines = fs.readFileSync(DB_PATH, 'utf8').split('\n').filter(l => l.trim());
    if (lines.length > MAX_ENTRIES) {
      // Keep last 50%
      const keep = lines.slice(-Math.floor(MAX_ENTRIES / 2));
      fs.writeFileSync(DB_PATH, keep.join('\n') + '\n');
    }
  } catch (e) {
    // Ignore rotation errors
  }
}

/**
 * Query tool stats
 */
function getStats(options = {}) {
  const { tool, since, hours = 24 } = options;

  let entries = [];
  try {
    const data = fs.readFileSync(DB_PATH, 'utf8');
    entries = data.split('\n')
      .filter(l => l.trim())
      .map(l => JSON.parse(l));
  } catch (e) {
    return { error: 'No data yet' };
  }

  // Filter by time
  const cutoff = since || new Date(Date.now() - hours * 60 * 60 * 1000).toISOString();
  entries = entries.filter(e => e.timestamp >= cutoff);

  // Filter by tool
  if (tool) {
    entries = entries.filter(e => e.tool === tool);
  }

  // Calculate stats
  const total = entries.length;
  const successful = entries.filter(e => e.outcome?.success).length;
  const failed = total - successful;
  const successRate = total > 0 ? (successful / total * 100).toFixed(1) : 0;

  // Group by error type
  const errors = {};
  entries.filter(e => !e.outcome?.success).forEach(e => {
    const errorType = e.outcome?.error || 'unknown';
    errors[errorType] = (errors[errorType] || 0) + 1;
  });

  // Average duration
  const durations = entries
    .filter(e => e.outcome?.duration_ms)
    .map(e => e.outcome.duration_ms);
  const avgDuration = durations.length > 0 
    ? (durations.reduce((a, b) => a + b, 0) / durations.length).toFixed(0)
    : 0;

  return {
    total,
    successful,
    failed,
    successRate: `${successRate}%`,
    avgDurationMs: avgDuration,
    errors,
    recentFailures: entries
      .filter(e => !e.outcome?.success)
      .slice(-5)
      .map(e => ({
        tool: e.tool,
        error: e.outcome?.error,
        timestamp: e.timestamp
      }))
  };
}

/**
 * Get patterns for a specific error
 */
function getPatterns(errorType) {
  let entries = [];
  try {
    const data = fs.readFileSync(DB_PATH, 'utf8');
    entries = data.split('\n')
      .filter(l => l.trim())
      .map(l => JSON.parse(l));
  } catch (e) {
    return [];
  }

  // Find entries with this error
  const failures = entries.filter(e => 
    e.outcome?.error?.toLowerCase().includes(errorType.toLowerCase())
  );

  // Group by tool + args pattern
  const patterns = {};
  failures.forEach(e => {
    const key = `${e.tool}:${JSON.stringify(e.args)}`;
    patterns[key] = (patterns[key] || 0) + 1;
  });

  return Object.entries(patterns)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10)
    .map(([pattern, count]) => ({ pattern, count }));
}

/**
 * Suggest fix based on history
 */
function suggestFix(tool, error) {
  let entries = [];
  try {
    const data = fs.readFileSync(DB_PATH, 'utf8');
    entries = data.split('\n')
      .filter(l => l.trim())
      .map(l => JSON.parse(l));
  } catch (e) {
    return null;
  }

  // Find similar failures that later succeeded
  const failures = entries.filter(e => 
    e.tool === tool && 
    !e.outcome?.success &&
    e.outcome?.error?.toLowerCase().includes(error?.toLowerCase())
  );

  // Look for retries that succeeded
  const suggestions = [];
  failures.forEach(failure => {
    const laterSuccess = entries.find(e => 
      e.tool === tool &&
      e.outcome?.success &&
      new Date(e.timestamp) > new Date(failure.timestamp) &&
      JSON.stringify(e.args) === JSON.stringify(failure.args)
    );

    if (laterSuccess) {
      suggestions.push({
        args: failure.args,
        succeededAfter: laterSuccess.timestamp
      });
    }
  });

  return suggestions.slice(0, 3);
}

// CLI
const command = process.argv[2];

switch (command) {
  case 'log':
    const record = JSON.parse(process.argv[3] || '{}');
    const id = logToolCall(record);
    console.log(JSON.stringify({ id }));
    break;

  case 'stats':
    const tool = process.argv[3];
    const hours = parseInt(process.argv[4]) || 24;
    console.log(JSON.stringify(getStats({ tool, hours }), null, 2));
    break;

  case 'patterns':
    const errorType = process.argv[3];
    console.log(JSON.stringify(getPatterns(errorType), null, 2));
    break;

  case 'suggest':
    const sTool = process.argv[3];
    const sError = process.argv[4];
    console.log(JSON.stringify(suggestFix(sTool, sError), null, 2));
    break;

  default:
    console.log(`
Tool Success Tracker

Usage:
  tool-tracker log '{"tool":"exec","args":{"cmd":"ls"},"outcome":{"success":true}}'
  tool-tracker stats [tool] [hours]
  tool-tracker patterns <error-type>
