#!/usr/bin/env node
/**
 * Exec wrapper with Tool Tracker integration
 * Usage: tracked-exec <command> [args...]
 */

const { execSync } = require('child_process');
const path = require('path');

// Import tool tracker
const { logToolCall } = require(path.join(__dirname, 'tool-tracker.js'));

const command = process.argv.slice(2).join(' ');

if (!command) {
  console.error('Usage: tracked-exec <command>');
  process.exit(1);
}

const startTime = Date.now();
let outcome;

try {
  const result = execSync(command, { 
    encoding: 'utf8',
    timeout: 30000,
    stdio: ['pipe', 'pipe', 'pipe']
  });

  outcome = {
    success: true,
    duration_ms: Date.now() - startTime,
    exit_code: 0
  };

  console.log(result);
} catch (error) {
  outcome = {
    success: false,
    duration_ms: Date.now() - startTime,
    exit_code: error.status || 1,
    error: error.message
  };

  if (error.stdout) console.log(error.stdout.toString());
  if (error.stderr) console.error(error.stderr.toString());
}

// Log to tool tracker
logToolCall({
  tool: 'exec',
  args: { command: command.substring(0, 500) }, // Truncate long commands
  outcome
});

process.exit(outcome.success ? 0 : 1);

