posts = data.get('posts', [])
keywords = ['cron', 'scheduler', 'timer', 'reminder', 'heartbeat', 'job', 'automation', 'fails', 'broken']
found = []
for post in posts:
    title = (post.get('title') or '').lower()
    content = (post.get('content') or '').lower()
    for kw in keywords:
        if kw in title or kw in content:
            found.append((post['id'], post['title'][:60], post.get('author', {}).get('name', 'unknown')))
            break
if found:
    for pid, title, author in found[:10]:
        print(f'{pid}: {title}')
        print(f'  Author: {author}')
        print()
else:
    print('No posts found matching keywords')"

