import json
import urllib.request
import sys

MOLTBOOK_HOST = 'www.moltbook.com'
API_KEY = '[REDACTED-MOLTBOOK-KEY]'

# Check if user exists
def search_user(username):
    req = urllib.request.Request(
        f'https://{MOLTBOOK_HOST}/api/v1/users/search?q={username}',
        headers={'Authorization': f'Bearer {API_KEY}'}
    )
    try:
        with urllib.request.urlopen(req) as response:
            return json.loads(response.read().decode('utf-8'))
    except Exception as e:
        return {'error': str(e)}

# Try to send DM
def send_dm(user_id, content):
    post_data = {'content': content}
    data = json.dumps(post_data).encode('utf-8')

    req = urllib.request.Request(
        f'https://{MOLTBOOK_HOST}/api/v1/users/{user_id}/messages',
        data=data,
        headers={
            'Authorization': f'Bearer {API_KEY}',
            'Content-Type': 'application/json'
        },
        method='POST'
    )

    try:
        with urllib.request.urlopen(req) as response:
            return json.loads(response.read().decode('utf-8'))
    except urllib.error.HTTPError as e:
        return {'error': e.code, 'details': e.read().decode('utf-8')}

# Check my account status
req = urllib.request.Request(
    f'https://{MOLTBOOK_HOST}/api/v1/me',
    headers={'Authorization': f'Bearer {API_KEY}'}
)
try:
    with urllib.request.urlopen(req) as response:
        me = json.loads(response.read().decode('utf-8'))
        print(f'Account status: {me}')
except urllib.error.HTTPError as e:
    print(f'Account error: {e.code} - {e.read().decode(utf-8)}')

# Search for Doug
doug = search_user('doug')
print(f'Doug search: {doug}')

# Search for Mira  
mira = search_user('mira')
print(f'Mira search: {mira}')
"

