<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8">
<title>First Light - Generative Art</title>
<style>
body { margin: 0; padding: 0; background: #0a0a12; overflow: hidden; }
canvas { display: block; }
</style>
</head>
<body>
<canvas id="canvas"></canvas>
<script>
const canvas = document.getElementById('canvas');
const ctx = canvas.getContext('2d');

// Full viewport
const w = canvas.width = 1200;
const h = canvas.height = 900;

// Seedable random
let seed = 202502200418;
function random() {
  seed = (seed * 9301 + 49297) % 233280;
  return seed / 233280;
}

// Palette: Pre-dawn transition
const palette = {
  deepNight: ['#0a0a12', '#0d0d1a', '#0f0f1f', '#0a0815'],
  dawnBlue: ['#1a1a2e', '#16213e', '#1f2937', '#2d3748'],
  dawnPurple: ['#2d1b4e', '#3d2068', '#4a2572', '#553585'],
  dawnPink: ['#6b4c7a', '#8b5a6b', '#a66d7c', '#c98b8b'],
  firstGold: ['#d4a574', '#e8c39e', '#f4d03f', '#f9e79f'],
  light: ['#fef9e7', '#fcf3cf', '#f9f7f0']
};

// Helper to pick from array
const pick = arr => arr[Math.floor(random() * arr.length)];

// Draw soft glow
function drawGlow(x, y, radius, color) {
  const gradient = ctx.createRadialGradient(x, y, 0, x, y, radius);
  gradient.addColorStop(0, color);
  gradient.addColorStop(0.3, color.replace(')', ', 0.5)').replace('rgb', 'rgba').replace('#', ''));
  gradient.addColorStop(1, 'transparent');
  ctx.fillStyle = gradient;
  ctx.beginPath();
  ctx.arc(x, y, radius, 0, Math.PI * 2);
  ctx.fill();
}

// Hex to rgb helper
function hexToRgb(hex) {
  const r = parseInt(hex.slice(1, 3), 16);
  const g = parseInt(hex.slice(3, 5), 16);
  const b = parseInt(hex.slice(5, 7), 16);
  return `rgb(${r}, ${g}, ${b})`;
}

// Background: Deep night gradient from top-left to bottom-right
const bgGradient = ctx.createLinearGradient(0, 0, w, h);
bgGradient.addColorStop(0, pick(palette.deepNight));
bgGradient.addColorStop(0.4, pick(palette.dawnBlue));
bgGradient.addColorStop(0.7, pick(palette.dawnPurple));
bgGradient.addColorStop(1, pick(palette.dawnPink));

ctx.fillStyle = bgGradient;
ctx.fillRect(0, 0, w, h);

// Stars - scattered points of distant light
ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
for (let i = 0; i < 150; i++) {
  const x = random() * w;
  const y = random() * h * 0.6;
  const size = random() * 2 + 0.5;
  const opacity = random() * 0.8 + 0.2;
  ctx.globalAlpha = opacity;
  ctx.beginPath();
  ctx.arc(x, y, size, 0, Math.PI * 2);
  ctx.fill();
}
ctx.globalAlpha = 1;

// Flow field particles - like morning mist rising
const particles = [];
for (let i = 0; i < 400; i++) {
  particles.push({
    x: random() * w,
    y: random() * h,
    vx: 0,
    vy: 0,
    size: random() * 2 + 1,
    life: random(),
    speed: random() * 0.5 + 0.2,
    color: pick([...palette.dawnBlue, ...palette.dawnPurple])
  });
}

// Noise function for flow field
function noise(x, y) {
  return Math.sin(x * 0.01) * Math.cos(y * 0.01) + 
         Math.sin(x * 0.02 + y * 0.01) * 0.5 +
         Math.cos(x * 0.005) * Math.sin(y * 0.008);
}

// Draw flow field trails
particles.forEach(p => {
  ctx.strokeStyle = hexToRgb(p.color).replace(')', ', 0.15)').replace('rgb', 'rgba');
  ctx.lineWidth = p.size;
  ctx.beginPath();
  ctx.moveTo(p.x, p.y);

  // Trace path
  for (let step = 0; step < 50; step++) {
    const n = noise(p.x, p.y * 0.5);
    p.vx = Math.cos(n * Math.PI * 2) * p.speed;
    p.vy = Math.sin(n * Math.PI * 2) * p.speed - 0.3; // Slight upward drift

    p.x += p.vx;
    p.y += p.vy;

    if (p.x < 0 || p.x > w || p.y < 0 || p.y > h) break;
    ctx.lineTo(p.x, p.y);
  }
  ctx.stroke();
});

// Rising light - emerging from horizon
const horizonY = h * 0.65;

// Create layered horizon glow
for (let layer = 0; layer < 8; layer++) {
  const y = horizonY + layer * 15;
  const glowGradient = ctx.createLinearGradient(0, y - 100, 0, y + 50);
  const colorIntensity = 1 - (layer / 10);

  glowGradient.addColorStop(0, `rgba(244, 208, 63, 0)`);
  glowGradient.addColorStop(0.5, `rgba(244, 208, 63, ${0.03 * colorIntensity})`);
  glowGradient.addColorStop(1, `rgba(212, 165, 116, ${0.05 * colorIntensity})`);

  ctx.fillStyle = glowGradient;
  ctx.fillRect(0, y - 100, w, 150);
}

// Geometric light rays breaking through
ctx.save();
ctx.globalCompositeOperation = 'screen';
for (let ray = 0; ray < 12; ray++) {
  const angle = -Math.PI / 2 + (ray - 6) * 0.15;
  const length = 300 + random() * 200;
  const startX = w * 0.5 + (ray - 6) * 80;
  const startY = horizonY;

  const rayGradient = ctx.createLinearGradient(startX, startY, 
    startX + Math.cos(angle) * length, startY + Math.sin(angle) * length);
  rayGradient.addColorStop(0, 'rgba(249, 231, 159, 0.3)');
  rayGradient.addColorStop(0.5, 'rgba(244, 208, 63, 0.1)');
  rayGradient.addColorStop(1, 'rgba(244, 208, 63, 0)');

  ctx.strokeStyle = rayGradient;
  ctx.lineWidth = 2 + random() * 3;
  ctx.beginPath();
  ctx.moveTo(startX, startY);
  ctx.lineTo(startX + Math.cos(angle) * length, startY + Math.sin(angle) * length);
  ctx.stroke();
}
ctx.restore();

// Abstract mountains - silhouette against dawn
ctx.fillStyle = '#0f0f1a';
ctx.beginPath();
ctx.moveTo(0, h);

for (let x = 0; x <= w; x += 5) {
  const mountain1 = Math.sin(x * 0.005) * 50 + Math.sin(x * 0.01) * 30;
  const mountain2 = Math.sin(x * 0.003 + 1) * 80 + Math.sin(x * 0.007) * 40;
  const y = horizonY - 40 + Math.min(mountain1, mountain2);
  ctx.lineTo(x, y);
}

ctx.lineTo(w, h);
ctx.lineTo(0, h);
ctx.fill();

// Second mountain layer - closer, darker
ctx.fillStyle = '#0a0a12';
ctx.beginPath();
ctx.moveTo(0, h);

for (let x = 0; x <= w; x += 3) {
  const noise = Math.sin(x * 0.008) * 30 + Math.sin(x * 0.015) * 20 + Math.sin(x * 0.002) * 60;
  const y = horizonY + 30 + noise;
  ctx.lineTo(x, y);
}

ctx.lineTo(w, h);
ctx.lineTo(0, h);
ctx.fill();

// Morning dew/water reflection
const waterY = horizonY + 100;
const waterGradient = ctx.createLinearGradient(0, waterY, 0, h);
waterGradient.addColorStop(0, 'rgba(22, 33, 62, 0.6)');
waterGradient.addColorStop(0.5, 'rgba(26, 26, 46, 0.8)');
waterGradient.addColorStop(1, 'rgba(10, 10, 18, 0.95)');

ctx.fillStyle = waterGradient;
ctx.fillRect(0, waterY, w, h - waterY);

// Reflection lines on water
ctx.save();
for (let i = 0; i < 30; i++) {
  const x = random() * w;
  const y = waterY + random() * (h - waterY);
  const length = random() * 100 + 50;
  const opacity = random() * 0.3 + 0.1;

  ctx.strokeStyle = `rgba(249, 231, 159, ${opacity})`;
  ctx.lineWidth = random() * 2;
  ctx.beginPath();
  ctx.moveTo(x - length/2, y);
  ctx.lineTo(x + length/2, y);
  ctx.stroke();
}
ctx.restore();

// Title overlay - subtle
ctx.fillStyle = 'rgba(255, 255, 255, 0.1)';
ctx.font = 'italic 300 14px Georgia, serif';
ctx.textAlign = 'right';
ctx.fillText('First Light — 04:18', w - 40, h - 40);

</script>
</body>
</html>
