#!/usr/bin/env python3
"""
Workspace Cleanup - Run every 2 days
Safely removes temporary/unneeded files
"""

import os
import glob
import time
from datetime import datetime, timedelta

WORKSPACE = "/data/workspace"
LOG_FILE = "/tmp/workspace-cleanup.log"

def log(msg):
    timestamp = datetime.now().strftime("%Y-%m-%d %H:%M:%S")
    entry = f"[{timestamp}] {msg}"
    print(entry)
    with open(LOG_FILE, "a") as f:
        f.write(entry + "\n")

def get_size_mb(path):
    """Get size of file/directory in MB"""
    try:
        if os.path.isfile(path):
            return os.path.getsize(path) / (1024 * 1024)
        total = 0
        for dirpath, dirnames, filenames in os.walk(path):
            for f in filenames:
                fp = os.path.join(dirpath, f)
                if os.path.exists(fp):
                    total += os.path.getsize(fp)
        return total / (1024 * 1024)
    except:
        return 0

def cleanup():
    log("=== Workspace Cleanup Started ===")
    total_freed = 0

    # 1. Clean old temporary files (older than 7 days)
    temp_patterns = [
        "/tmp/*.png",
        "/tmp/*.jpg", 
        "/tmp/*.jpeg",
        "/tmp/*.log",
        "/tmp/hydroxide*.log",
        "/tmp/openclaw*.log",
    ]

    for pattern in temp_patterns:
        for f in glob.glob(pattern):
            try:
                age_days = (time.time() - os.path.getmtime(f)) / 86400
                if age_days > 7:
                    size = get_size_mb(f)
                    os.remove(f)
                    total_freed += size
                    log(f"Removed old temp: {f} ({size:.1f} MB, {age_days:.0f} days old)")
            except Exception as e:
                log(f"Could not remove {f}: {e}")

    # 2. Clean browser screenshots older than 3 days
    browser_media = "/data/media/browser"
    if os.path.exists(browser_media):
        for f in os.listdir(browser_media):
            path = os.path.join(browser_media, f)
            try:
                age_days = (time.time() - os.path.getmtime(path)) / 86400
                if age_days > 3:
                    size = get_size_mb(path)
                    os.remove(path)
                    total_freed += size
                    log(f"Removed old screenshot: {f} ({size:.1f} MB)")
            except Exception as e:
                pass

    # 3. Truncate log files if they get too big (>10MB)
    log_files = [
        "/tmp/hydroxide.log",
        "/tmp/email-combined.log",
        "/tmp/email-health.log",
    ]

    for log_file in log_files:
        if os.path.exists(log_file):
            size = get_size_mb(log_file)
            if size > 10:
                try:
                    # Keep last 1000 lines
                    with open(log_file, 'r') as f:
                        lines = f.readlines()
                    with open(log_file, 'w') as f:
                        f.writelines(lines[-1000:])
                    log(f"Truncated {log_file} (was {size:.1f} MB)")
                except Exception as e:
                    log(f"Could not truncate {log_file}: {e}")

    # 4. Clean old debug/session files
    debug_patterns = [
        f"{WORKSPACE}/debug/*.log",
    ]

    for pattern in debug_patterns:
        for f in glob.glob(pattern):
            try:
                age_days = (time.time() - os.path.getmtime(f)) / 86400
                if age_days > 14:
                    size = get_size_mb(f)
                    os.remove(f)
                    total_freed += size
                    log(f"Removed old debug file: {f} ({size:.1f} MB)")
            except:
                pass

    # 5. Report disk space
    stat = os.statvfs(WORKSPACE)
    free_gb = (stat.f_bavail * stat.f_frsize) / (1024**3)
    total_gb = (stat.f_blocks * stat.f_frsize) / (1024**3)
    used_pct = (1 - stat.f_bavail/stat.f_blocks) * 100

    log(f"=== Cleanup Complete ===")
    log(f"Freed: {total_freed:.1f} MB")
    log(f"Disk: {free_gb:.1f} GB free / {total_gb:.1f} GB total ({used_pct:.1f}% used)")

    return total_freed

if __name__ == "__main__":
    cleanup()

