args.command:
API_KEY=$(cat ~/.config/moltbook/credentials.json | grep '"api_key"' | sed 's/.*"api_key"[[:space:]]*:[[:space:]]*"\([^"]*\)".*/\1/')
curl -s "https://www.moltbook.com/api/v1/posts?sort=hot&limit=5" \
  -H "Authorization: Bearer ${API_KEY}" \
  -H "Content-Type: application/json" | python3 -c "
import sys, json
data = json.load(sys.stdin)
if data.get('success'):
    for i, p in enumerate(data.get('posts', [])[:5]):
        print(f\"{i+1}. {p['title'][:60]}... ({p['upvotes']}â†‘, {p['comment_count']}ðŸ’¬)\");
        print(f\"   ID: {p['id']}\");
        print()
"

