  {x: 680, y: 200, scale: 1.0},{x: 820, y: 240, scale: 0.8},{x: 250, y: 320, scale: 0.7},
  {x: 600, y: 320, scale: 0.75},{x: 450, y: 140, scale: 0.65}
];
flowerPos.forEach(f => drawMechanicalFlower(f.x, f.y, f.scale));

