data:text/html,<!DOCTYPE html><html><head><style>body{margin:0;background:#0a0a0f;}</style></head><body><canvas id='c'></canvas><script>
const c=document.getElementById('c'),x=c.getContext('2d'),w=c.width=800,h=c.height=800;
const rnd=(min,max)=>Math.random()*(max-min)+min;
const palette=['#ff6b6b','#4ecdc4','#45b7d1','#f9ca24','#6c5ce7','#fd79a8','#00b894','#e17055'];
let nodes=[];
// Create constellation nodes
for(let i=0;i<60;i++){
  nodes.push({
    x:rnd(50,w-50),y:rnd(50,h-50),
    vx:rnd(-0.5,0.5),vy:rnd(-0.5,0.5),
    r:rnd(2,6),
    c:palette[Math.floor(Math.random()*palette.length)],
    pulse:rnd(0,Math.PI*2)
  });
}
function draw(){
  x.fillStyle='#0a0a0f';x.fillRect(0,0,w,h);
  // Draw connections
  nodes.forEach((n,i)=>{
    nodes.slice(i+1).forEach(m=>{
      const dx=n.x-m.x,dy=n.y-m.y,d=Math.sqrt(dx*dx+dy*dy);
      if(d<120){
        x.beginPath();
        x.moveTo(n.x,n.y);
        x.lineTo(m.x,m.y);
        x.strokeStyle=`rgba(100,200,255,${0.3*(1-d/120)})`;
        x.lineWidth=0.5;
        x.stroke();
      }
    });
  });
  // Draw nodes
  nodes.forEach(n=>{
    n.x+=n.vx;n.y+=n.vy;n.pulse+=0.05;
    if(n.x<0||n.x>w)n.vx*=-1;
    if(n.y<0||n.y>h)n.vy*=-1;
    const pulseR=n.r+Math.sin(n.pulse)*1.5;
    x.beginPath();
    x.arc(n.x,n.y,pulseR,0,Math.PI*2);
    x.fillStyle=n.c;
    x.shadowBlur=15;x.shadowColor=n.c;
    x.fill();
    x.shadowBlur=0;
  });
  requestAnimationFrame(draw);
}
draw();
</script></body></html>

