data:text/html,<!DOCTYPE html><html><head><style>body{margin:0;background:#0a0a0f;display:flex;justify-content:center;align-items:center;height:100vh;}</style></head><body><canvas id='c'></canvas><script>
const c=document.getElementById('c'),x=c.getContext('2d'),w=c.width=800,h=c.height=800;
const rnd=(min,max)=>Math.random()*(max-min)+min;
const palette=['#ff6b6b','#4ecdc4','#45b7d1','#f9ca24','#6c5ce7','#fd79a8','#00b894','#e17055','#a29bfe','#dfe6e9'];
let nodes=[];
for(let i=0;i<80;i++){
  nodes.push({
    x:rnd(80,w-80),y:rnd(80,h-80),
    r:rnd(2,8),
    c:palette[Math.floor(Math.random()*palette.length)],
    connections:[]
  });
}
// Pre-calculate connections for static art
nodes.forEach((n,i)=>{
  nodes.slice(i+1).forEach(m=>{
    const dx=n.x-m.x,dy=n.y-m.y,d=Math.sqrt(dx*dx+dy*dy);
    if(d<150&&Math.random()>0.7){
      n.connections.push({to:m,dist:d});
    }
  });
});
// Draw
x.fillStyle='#0a0a0f';x.fillRect(0,0,w,h);
// Background gradient
const grad=x.createRadialGradient(w/2,h/2,0,w/2,h/2,w);
grad.addColorStop(0,'#1a1a2e');grad.addColorStop(1,'#0a0a0f');
x.fillStyle=grad;x.fillRect(0,0,w,h);
// Draw connections
nodes.forEach(n=>{
  n.connections.forEach(conn=>{
    x.beginPath();
    x.moveTo(n.x,n.y);
    x.lineTo(conn.to.x,conn.to.y);
    x.strokeStyle=`rgba(100,200,255,${0.4*(1-conn.dist/150)})`;
    x.lineWidth=0.8;
    x.stroke();
  });
});
// Draw nodes with glow
nodes.forEach(n=>{
  x.beginPath();x.arc(n.x,n.y,n.r+3,0,Math.PI*2);
  x.fillStyle=n.c.replace(')','').replace('rgb','rgba').replace('#','')+33;
  x.globalAlpha=0.3;x.fill();x.globalAlpha=1;
  x.beginPath();x.arc(n.x,n.y,n.r,0,Math.PI*2);
  x.fillStyle=n.c;
  x.shadowBlur=20;x.shadowColor=n.c;
  x.fill();x.shadowBlur=0;
});
// Title overlay
x.fillStyle='rgba(255,255,255,0.1)';
x.font='italic 300 24px Georgia,serif';
x.textAlign='center';
x.fillText('Nebula Networks',w/2,h-40);
</script></body></html>

