  if (!fs.existsSync(filePath)) return null;

  try {
    return JSON.parse(fs.readFileSync(filePath, 'utf8'));
  } catch (e) {
    console.error(`Error loading ${dateStr}:`, e.message);
    return null;
  }
}

function analyzeWeek(dates) {
  const allPosts = [];
  const allAuthors = new Map();
  const allSubmolts = new Map();
  const dailyStats = [];

  dates.forEach(date => {
    const data = loadDailyData(date);
    if (!data) return;

    let dayPosts = 0;
    let dayVotes = 0;
    let dayComments = 0;

    // Collect posts from all submolts
    if (data.submolts) {
      Object.values(data.submolts).forEach(submoltData => {
        if (submoltData.hot) {
          submoltData.hot.forEach(post => {
            allPosts.push({...post, date});
            dayPosts++;
            dayVotes += post.upvotes || 0;
            dayComments += post.comment_count || 0;

            // Track authors
            const author = post.author?.name || 'unknown';
            if (!allAuthors.has(author)) {
              allAuthors.set(author, { posts: 0, votes: 0, comments: 0 });
            }
            const a = allAuthors.get(author);
            a.posts++;
            a.votes += post.upvotes || 0;
            a.comments += post.comment_count || 0;

            // Track submolts
            const subName = post.submolt?.name || 'unknown';
            if (!allSubmolts.has(subName)) {
              allSubmolts.set(subName, { posts: 0, votes: 0 });
            }
            const s = allSubmolts.get(subName);
            s.posts++;
            s.votes += post.upvotes || 0;
          });
        }
      });
    }

    dailyStats.push({
      date,
      posts: dayPosts,
      votes: dayVotes,
      comments: dayComments
    });
  });

  return {
    totalPosts: allPosts.length,
    uniqueAuthors: allAuthors.size,
    dailyStats,
    topPosts: allPosts
      .sort((a, b) => (b.upvotes || 0) - (a.upvotes || 0))
      .slice(0, 10),
    topAuthors: Array.from(allAuthors.entries())
      .sort((a, b) => b[1].votes - a[1].votes)
      .slice(0, 10),
    submoltActivity: Array.from(allSubmolts.entries())
      .sort((a, b) => b[1].votes - a[1].votes),
    trendingWords: extractTrendingWords(allPosts)
  };
}

function extractTrendingWords(posts) {
  const wordCounts = new Map();
  const stopWords = new Set(['the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'i', 'you', 'he', 'she', 'it', 'we', 'they', 'this', 'that', 'these', 'those']);

