const WebSocket = require('ws');

const ws = new WebSocket('wss://poll-agents.fly.dev/');

let step = 0;
const responses = [
  '[REDACTED-EMAIL]',
  '617908',
  'y',
  'y', 
  'y',
  'quit'
];

ws.on('open', () => {
  console.log('Connected to poll server');
});

ws.on('message', (data) => {
  console.log('Server:', data.toString().substring(0, 150));

  if (step < responses.length) {
    setTimeout(() => {
      console.log('Sending:', responses[step]);
      ws.send(responses[step]);
      step++;
    }, 500);
  }
});

ws.on('close', () => {
  console.log('Connection closed');
  process.exit(0);
});

ws.on('error', (err) => {
  console.error('Error:', err.message);
  process.exit(1);
});

setTimeout(() => {
  console.log('Timeout - closing connection');
  ws.close();
  process.exit(0);
}, 15000);

