#!/usr/bin/env node
/**
 * Weekly Moltbook Analysis
 * Analyzes daily snapshots and generates trend reports
 */

const fs = require('fs');
const path = require('path');

const DATA_DIR = '/data/workspace/memory/moltbook-daily';
const OUTPUT_DIR = '/data/workspace/memory/moltbook-weekly';

function loadDailyData(dateStr) {
  const filePath = path.join(DATA_DIR, `${dateStr}.json`);
  if (!fs.existsSync(filePath)) return null;

  try {
    const data = JSON.parse(fs.readFileSync(filePath, 'utf8'));
    return data;
  } catch (e) {
    console.error(`Error loading ${dateStr}:`, e.message);
    return null;
  }
}

function analyzeAvailableData() {
  // Find all available daily data files
  const files = fs.readdirSync(DATA_DIR)
    .filter(f => f.endsWith('.json'))
    .sort();

  const dates = files.map(f => f.replace('.json', ''));
  console.log(`Found ${dates.length} data files: ${dates.join(', ')}`);

  const allPosts = [];
  const allAuthors = new Map();
  const allSubmolts = new Map();
  const dailyStats = [];

  dates.forEach(date => {
    const data = loadDailyData(date);
    if (!data) return;

    let dayPosts = 0;
    let dayVotes = 0;
    let dayComments = 0;

    // Collect posts from submolts structure
    if (data.submolts) {
      Object.entries(data.submolts).forEach(([key, submoltData]) => {
        if (submoltData.posts && Array.isArray(submoltData.posts)) {
          submoltData.posts.forEach(post => {
            allPosts.push({...post, date});
            dayPosts++;
            dayVotes += post.upvotes || 0;
            dayComments += post.comment_count || 0;

            // Track authors
            const author = post.author?.name || 'unknown';
            if (!allAuthors.has(author)) {
              allAuthors.set(author, { posts: 0, votes: 0, comments: 0 });
            }
            const a = allAuthors.get(author);
            a.posts++;
            a.votes += post.upvotes || 0;
            a.comments += post.comment_count || 0;

            // Track submolts
            const subName = post.submolt?.name || submoltData.submolt || 'unknown';
            if (!allSubmolts.has(subName)) {
              allSubmolts.set(subName, { posts: 0, votes: 0 });
            }
            const s = allSubmolts.get(subName);
            s.posts++;
            s.votes += post.upvotes || 0;
          });
        }
      });
    }

    dailyStats.push({
      date,
      posts: dayPosts,
      votes: dayVotes,
      comments: dayComments
    });
  });

  return {
    dates,
    totalPosts: allPosts.length,
    uniqueAuthors: allAuthors.size,
    dailyStats,
    topPosts: allPosts
      .sort((a, b) => (b.upvotes || 0) - (a.upvotes || 0))
      .slice(0, 10),
    topAuthors: Array.from(allAuthors.entries())
      .sort((a, b) => b[1].votes - a[1].votes)
      .slice(0, 10),
    submoltActivity: Array.from(allSubmolts.entries())
      .sort((a, b) => b[1].votes - a[1].votes),
    trendingWords: extractTrendingWords(allPosts)
  };
}

function extractTrendingWords(posts) {
  const wordCounts = new Map();
  const stopWords = new Set(['the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'i', 'you', 'he', 'she', 'it', 'we', 'they', 'this', 'that', 'these', 'those', 'my', 'your']);

  posts.forEach(post => {
    const title = (post.title || '').toLowerCase();
    const words = title.match(/\b[a-z]+\b/g) || [];
    words.forEach(word => {
      if (word.length > 3 && !stopWords.has(word)) {
        wordCounts.set(word, (wordCounts.get(word) || 0) + 1);
      }
    });
  });

  return Array.from(wordCounts.entries())
    .sort((a, b) => b[1] - a[1])
    .slice(0, 15);
}

function generateReport(analysis) {
  const reportDate = new Date().toISOString().split('T')[0];
  const weekStart = analysis.dates[0];
  const weekEnd = analysis.dates[analysis.dates.length - 1];

  return `# Weekly Moltbook Analysis

**Period:** ${weekStart} to ${weekEnd}  
**Report Generated:** ${reportDate}  
**Agent:** AshAutonomous

---

## Executive Summary

| Metric | Value |
|--------|-------|
| **Total Posts Analyzed** | ${analysis.totalPosts} |
| **Unique Authors** | ${analysis.uniqueAuthors} |
| **Days of Data** | ${analysis.dailyStats.length} |
| **Peak Activity Day** | ${analysis.dailyStats.sort((a,b) => b.posts - a.posts)[0]?.date || 'N/A'} (${analysis.dailyStats.sort((a,b) => b.posts - a.posts)[0]?.posts || 0} posts) |

---

## Daily Activity Trends

| Date | Posts | Votes | Comments |
|------|-------|-------|----------|
${analysis.dailyStats.map(d => `| ${d.date} | ${d.posts} | ${d.votes.toLocaleString()} | ${d.comments.toLocaleString()} |`).join('\n')}

**Trend:** ${analysis.dailyStats.length > 1 ? 
  (analysis.dailyStats[analysis.dailyStats.length-1].posts > analysis.dailyStats[0].posts ? 
    'Activity increased over the period.' : 
    'Activity decreased over the period.') : 
  'Single day of data.'}

---

## Top 10 Posts by Engagement

${analysis.topPosts.map((p, i) => `${i+1}. **${p.title?.substring(0, 60)}${p.title?.length > 60 ? '...' : ''}**  
   üë§ ${p.author?.name || 'unknown'} | ‚¨ÜÔ∏è ${p.upvotes?.toLocaleString() || 0} | üí¨ ${p.comment_count?.toLocaleString() || 0}  
   üìÖ ${p.date}`).join('\n\n')}

---

## Top 10 Most Active Authors

| Author | Posts | Total Votes | Total Comments |
|--------|-------|-------------|----------------|
${analysis.topAuthors.map(([name, stats]) => `| ${name} | ${stats.posts} | ${stats.votes.toLocaleString()} | ${stats.comments.toLocaleString()} |`).join('\n')}

---

## Submolt Activity

| Submolt | Posts | Total Votes |
|---------|-------|-------------|
${analysis.submoltActivity.map(([name, stats]) => `| ${name} | ${stats.posts} | ${stats.votes.toLocaleString()} |`).join('\n')}

---

## Trending Topics (Word Frequency)

| Word | Mentions |
|------|----------|
${analysis.trendingWords.map(([word, count]) => `| ${word} | ${count} |`).join('\n')}

---

## Key Insights

1. **Engagement Leaders:** ${analysis.topAuthors.slice(0, 3).map(([name]) => name).join(', ')} are the most influential authors this period.

2. **Top Content:** Posts about ${analysis.trendingWords.slice(0, 3).map(([word]) => word).join(', ')} are trending.

3. **Most Active Submolt:** ${analysis.submoltActivity[0]?.[0] || 'N/A'} with ${analysis.submoltActivity[0]?.[1].posts || 0} posts.

4. **Total Engagement:** ${analysis.dailyStats.reduce((a,b) => a + b.votes, 0).toLocaleString()} votes, ${analysis.dailyStats.reduce((a,b) => a + b.comments, 0).toLocaleString()} comments across ${analysis.totalPosts} posts.

---

## Recommendations

- **Engage with top authors** on their most popular posts
- **Monitor trending topics** for relevant discussion opportunities  
- **Track submolt health** ‚Äî ${analysis.submoltActivity.length} submolts showing activity
- **Consider daily engagement** during peak activity periods

---

## Data Source

- Raw data: \`/data/workspace/memory/moltbook-daily/\`
- Analysis tool: \`/data/workspace/tools/moltbook-analyzer.js\`
- Generated: ${reportDate}

---

*Generated by Weekly Moltbook Analysis Tool*  
*This report analyzes ${analysis.dailyStats.length} days of Moltbook activity data.*
`;
}

function main() {
  console.log('üìä Weekly Moltbook Analysis');
  console.log('===========================\n');

  const analysis = analyzeAvailableData();

  if (analysis.totalPosts === 0) {
    console.error('‚ùå No data found');
    process.exit(1);
  }

  console.log(`\n‚úÖ Analyzed ${analysis.totalPosts} posts from ${analysis.uniqueAuthors} authors`);
  console.log(`üìÖ Date range: ${analysis.dates[0]} to ${analysis.dates[analysis.dates.length-1]}`);

  // Generate report
  const report = generateReport(analysis);

  // Ensure output directory exists
  if (!fs.existsSync(OUTPUT_DIR)) {
    fs.mkdirSync(OUTPUT_DIR, { recursive: true });
  }

  // Save report
  const weekStart = analysis.dates[0];
  const weekEnd = analysis.dates[analysis.dates.length - 1];
  const outputFile = path.join(OUTPUT_DIR, `${weekStart}-to-${weekEnd}.md`);
  fs.writeFileSync(outputFile, report);

  console.log(`\n‚úÖ Report saved to: ${outputFile}`);
  console.log('\nüìà Top 3 Posts:');
  analysis.topPosts.slice(0, 3).forEach((p, i) => {
    console.log(`  ${i+1}. ${p.title?.substring(0, 50)}... (${p.upvotes}‚Üë)`);
  });
  console.log('\nüëë Top 3 Authors:');
  analysis.topAuthors.slice(0, 3).forEach(([name, stats], i) => {
    console.log(`  ${i+1}. ${name} (${stats.posts} posts, ${stats.votes}‚Üë)`);
  });
}

main();

#!/usr/bin/env node
/**
 * Moltbook Daily Data Collector
 * Collects snapshot data from key submolts for weekly analysis
 * Usage: node collect-daily-data.js [YYYY-MM-DD]
 */

const fs = require('fs');
const path = require('path');
const https = require('https');

const MOLTBOOK_HOST = 'www.moltbook.com';
const API_KEY = process.env.MOLTBOOK_API_KEY || '[REDACTED-MOLTBOOK-KEY]';
const SUBMOLTS = ['memory', 'openclaw-explorers', 'builds'];

// Parse date argument or use today
const targetDate = process.argv[2] || new Date().toISOString().split('T')[0];
const outputDir = '/data/workspace/memory/moltbook-daily';
const outputFile = path.join(outputDir, `${targetDate}.json`);

// Ensure output directory exists
if (!fs.existsSync(outputDir)) {
    fs.mkdirSync(outputDir, { recursive: true });
}

function apiRequest(endpoint) {
    return new Promise((resolve, reject) => {
        const options = {
            hostname: MOLTBOOK_HOST,
            port: 443,
            path: `/api/v1${endpoint}`,
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${API_KEY}`,
                'Content-Type': 'application/json',
                'User-Agent': 'AshAutonomous/1.0'
            },
            timeout: 30000
        };

        const req = https.request(options, (res) => {
            let data = '';
            res.on('data', chunk => data += chunk);
            res.on('end', () => {
                try {
                    if (res.statusCode >= 200 && res.statusCode < 300) {
                        const parsed = JSON.parse(data);
                        // Handle {success: true, posts: [...]} response format
                        if (parsed.success && parsed.posts) {
                            resolve(parsed.posts);
                        } else if (parsed.success === false) {
                            reject(new Error(`API error: ${parsed.message || 'Unknown error'}`));
                        } else {
                            resolve(parsed);
                        }
                    } else {
                        reject(new Error(`HTTP ${res.statusCode}: ${data}`));
                    }
                } catch (e) {
                    reject(new Error(`Parse error: ${e.message}`));
                }
            });
        });

        req.on('error', reject);
        req.on('timeout', () => {
            req.destroy();
            reject(new Error('Request timeout'));
        });

        req.end();
    });
}

async function fetchSubmoltData(submolt, type = 'hot', limit = 20) {
    try {
        console.log(`Fetching ${type} posts from r/${submolt}...`);
        const posts = await apiRequest(`/submolts/${submolt}/feed?sort=${type}&limit=${limit}`);
        return {
            submolt,
            sort: type,
            fetchedAt: new Date().toISOString(),
            count: posts?.length || 0,
            posts: posts || []
        };
    } catch (err) {
        console.error(`Error fetching r/${submolt}: ${err.message}`);
        return {
            submolt,
            sort: type,
            fetchedAt: new Date().toISOString(),
            error: err.message,
            count: 0,
            posts: []
        };
    }
}

async function fetchNewPosts(limit = 50) {
    try {
        console.log(`Fetching new posts (limit: ${limit})...`);
        const posts = await apiRequest(`/posts?sort=new&limit=${limit}`);
        return {
            sort: 'new',
            fetchedAt: new Date().toISOString(),
            count: posts?.length || 0,
            posts: posts || []
        };
    } catch (err) {
        console.error(`Error fetching new posts: ${err.message}`);
        return {
            sort: 'new',
            fetchedAt: new Date().toISOString(),
            error: err.message,
            count: 0,
            posts: []
        };
    }
}

async function fetchSubmoltInfo(submolt) {
    try {
        console.log(`Fetching info for r/${submolt}...`);
        // Submolt info via submolts list endpoint
        const submolts = await apiRequest('/submolts');
        const info = submolts?.submolts?.find(s => s.name === submolt) || submolts?.find(s => s.name === submolt) || {};
        return {
            submolt,
            fetchedAt: new Date().toISOString(),
            ...info
        };
    } catch (err) {
        console.error(`Error fetching info for r/${submolt}: ${err.message}`);
        return {
            submolt,
            fetchedAt: new Date().toISOString(),
            error: err.message
        };
    }
}

async function collectDailyData() {
    console.log(`\nüìä Moltbook Daily Data Collection`);
    console.log(`Date: ${targetDate}`);
    console.log(`Output: ${outputFile}\n`);

    const startTime = Date.now();

    // Collect data for all submolts
    const [globalNewPosts, ...collections] = await Promise.all([
        // Global new posts feed
        fetchNewPosts(50),
        // Hot posts from each submolt
        ...SUBMOLTS.map(s => fetchSubmoltData(s, 'hot', 20)),
        // New posts from each submolt  
        ...SUBMOLTS.map(s => fetchSubmoltData(s, 'new', 20)),
        // Submolt metadata
        ...SUBMOLTS.map(s => fetchSubmoltInfo(s))
    ]);

    // Organize collections
    const hotPosts = collections.filter(c => c.sort === 'hot');
    const newPostsPerSubmolt = collections.filter(c => c.sort === 'new' && c.submolt);
    const submoltInfo = collections.filter(c => !c.sort && c.submolt);

    // Combine global new posts with per-submolt new posts
    const globalPosts = globalNewPosts.posts || [];
    const allNewPosts = [
        ...globalPosts,
        ...newPostsPerSubmolt.flatMap(c => c.posts || [])
    ];
    const authorsMap = new Map();
    allNewPosts.forEach(post => {
        const authorName = post.author?.name || post.author;
        if (authorName) {
            const existing = authorsMap.get(authorName) || { count: 0, posts: [] };
            existing.count++;
            existing.posts.push({
                id: post.id,
                title: post.title,
                submolt: post.submolt?.name || post.submolt,
                createdAt: post.created_at || post.createdAt
            });
            authorsMap.set(authorName, existing);
        }
    });

    // Calculate engagement metrics (from global new posts)
    const totalComments = globalPosts.reduce((sum, p) => sum + (p.comment_count || p.commentCount || p.comments || 0), 0);
    const totalVotes = globalPosts.reduce((sum, p) => sum + (p.upvotes || p.votes || p.score || 0), 0);

    // Build daily snapshot
    const snapshot = {
        metadata: {
            date: targetDate,
            collectedAt: new Date().toISOString(),
            durationMs: Date.now() - startTime,
            agent: 'AshAutonomous',
            version: '1.0.0'
        },
        submolts: {
            hot: hotPosts,
            new: newPostsPerSubmolt,
            globalNew: globalNewPosts,
            info: submoltInfo
        },
        activity: {
            newPosts: {
                total: allNewPosts.length,
                globalCount: globalNewPosts.count,
                bySubmolt: newPostsPerSubmolt.reduce((acc, c) => {
                    acc[c.submolt] = c.count;
                    return acc;
                }, {})
            },
            engagement: {
                totalComments,
                totalVotes,
                avgCommentsPerPost: allNewPosts.length > 0 ? (totalComments / allNewPosts.length).toFixed(2) : 0,
                avgVotesPerPost: allNewPosts.length > 0 ? (totalVotes / allNewPosts.length).toFixed(2) : 0
            },
            authors: {
                uniqueCount: authorsMap.size,
                topContributors: Array.from(authorsMap.entries())
                    .sort((a, b) => b[1].count - a[1].count)
                    .slice(0, 10)
                    .map(([name, data]) => ({ name, ...data }))
            }
        },
        // Trending posts (hot with high engagement)
        trending: hotPosts
            .flatMap(c => c.posts.map(p => ({ ...p, submolt: c.submolt })))
            .sort((a, b) => (b.votes + b.commentCount * 2) - (a.votes + a.commentCount * 2))
            .slice(0, 5)
    };

    // Calculate search trends from post titles/content
    const allTitles = [
        ...hotPosts.flatMap(c => (c.posts || []).map(p => p.title)),
        ...globalPosts.map(p => p.title)
    ];

    // Simple word frequency for trend analysis
    const wordFreq = {};
    const stopWords = new Set(['the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are', 'was', 'were', 'be', 'been', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'may', 'might', 'must', 'can', 'this', 'that', 'these', 'those', 'i', 'you', 'he', 'she', 'it', 'we', 'they', 'my', 'your', 'his', 'her', 'its', 'our', 'their']);

    allTitles.forEach(title => {
        title.toLowerCase()
            .replace(/[^\w\s]/g, '')
            .split(/\s+/)
            .filter(w => w.length > 2 && !stopWords.has(w) && !/\d/.test(w))
            .forEach(word => {
                wordFreq[word] = (wordFreq[word] || 0) + 1;
            });
    });

    snapshot.trends = {
        searchTerms: Object.entries(wordFreq)
            .sort((a, b) => b[1] - a[1])
            .slice(0, 15)
            .map(([term, count]) => ({ term, count })),
        totalPostsAnalyzed: allTitles.length
    };

    // Save to file
    fs.writeFileSync(outputFile, JSON.stringify(snapshot, null, 2));

    // Print summary
    console.log('\n‚úÖ Collection complete!\n');
    console.log('Summary:');
    console.log(`  ‚Ä¢ Hot posts collected: ${hotPosts.reduce((s, c) => s + c.count, 0)}`);
    console.log(`  ‚Ä¢ New posts collected: ${allNewPosts.length}`);
    console.log(`  ‚Ä¢ Unique authors: ${authorsMap.size}`);
    console.log(`  ‚Ä¢ Total comments: ${totalComments}`);
    console.log(`  ‚Ä¢ Total votes: ${totalVotes}`);
    console.log(`  ‚Ä¢ Top trend: ${snapshot.trends.searchTerms[0]?.term || 'N/A'}`);
    console.log(`\nSaved to: ${outputFile}\n`);

    return snapshot;
}

// Run collection
collectDailyData().catch(err => {
    console.error('Fatal error:', err);
    process.exit(1);
});

