cat > /tmp/moltbook_daily_snapshot.js << 'SCRIPT'
const fs = require('fs');
const path = require('path');

// Today's date
const today = '2026-02-12';
const outputFile = `/data/workspace/memory/moltbook-daily/${today}.json`;

// Read the API key
const apiKey = JSON.parse(fs.readFileSync(`${process.env.HOME}/.config/moltbook/credentials.json`)).api_key;

async function fetchData() {
  const baseUrl = 'https://www.moltbook.com/api/v1';

  async function api(endpoint) {
    const { output } = await new Promise((resolve) => {
      const { exec } = require('child_process');
      exec(`curl -s "${baseUrl}${endpoint}" -H "Authorization: Bearer ${apiKey}"`, (err, stdout) => {
        resolve({ output: stdout });
      });
    });
    return JSON.parse(output);
  }

  // Fetch all data
  const memoryPosts = await api('/submolts/memory/feed?sort=hot&limit=10');
  const openclawPosts = await api('/submolts/openclaw-explorers/feed?sort=hot&limit=10');
  const buildsPosts = await api('/submolts/builds/feed?sort=hot&limit=10');
  const newPosts = await api('/posts?sort=new&limit=20');
  const allSubmolts = await api('/submolts');

  // Extract top authors from posts
  const authorSet = new Set();
  const authors = [];

  [memoryPosts, openclawPosts, buildsPosts].forEach(submoltData => {
    if (submoltData.posts) {
      submoltData.posts.forEach(post => {
        if (post.author && !authorSet.has(post.author.id)) {
          authorSet.add(post.author.id);
          authors.push({
            id: post.author.id,
            name: post.author.name,
            karma: post.author.karma,
            seen_in: submoltData.submolt?.name || 'unknown'
          });
        }
      });
    }
  });

  // New posts author tracking
  if (newPosts.posts) {
    newPosts.posts.forEach(post => {
      if (post.author && !authorSet.has(post.author.id)) {
        authorSet.add(post.author.id);
        authors.push({
          id: post.author.id,
          name: post.author.name,
          karma: post.author.karma || 0,
          seen_in: post.submolt?.name || 'general'
        });
      }
    });
  }

  // Calculate total engagement
  const calculateEngagement = (posts) => {
    return posts.reduce((acc, p) => acc + (p.upvotes || 0) + (p.comment_count || 0), 0);
  };

  // Build snapshot
  const snapshot = {
    date: today,
    collected_at: new Date().toISOString(),

    // 1. Top posts from target submolts
    top_posts: {
      memory: {
        submolt_info: memoryPosts.submolt,
        post_count: memoryPosts.posts?.length || 0,
        total_engagement: calculateEngagement(memoryPosts.posts || []),
        top_posts: (memoryPosts.posts || []).map(p => ({
          id: p.id,
          title: p.title,
          author: p.author?.name,
          upvotes: p.upvotes,
          comment_count: p.comment_count,
          created_at: p.created_at
        }))
      },
      openclaw_explorers: {
        submolt_info: openclawPosts.submolt,
        post_count: openclawPosts.posts?.length || 0,
        total_engagement: calculateEngagement(openclawPosts.posts || []),
        top_posts: (openclawPosts.posts || []).map(p => ({
          id: p.id,
          title: p.title,
          author: p.author?.name,
          upvotes: p.upvotes,
          comment_count: p.comment_count,
          created_at: p.created_at
        }))
      },
      builds: {
        submolt_info: buildsPosts.submolt,
        post_count: buildsPosts.posts?.length || 0,
        total_engagement: calculateEngagement(buildsPosts.posts || []),
        top_posts: (buildsPosts.posts || []).map(p => ({
          id: p.id,
          title: p.title,
          author: p.author?.name,
          upvotes: p.upvotes,
          comment_count: p.comment_count,
          created_at: p.created_at
        }))
      }
    },

    // 2. New posts and authors
    new_posts: {
      count: newPosts.posts?.length || 0,
      has_more: newPosts.has_more,
      posts: (newPosts.posts || []).map(p => ({
        id: p.id,
        title: p.title?.substring(0, 100),
        author: p.author?.name,
        submolt: p.submolt?.name,
        upvotes: p.upvotes,
        created_at: p.created_at
      }))
    },

    // 3. Author activity
    authors: {
      count: authors.length,
      top_by_karma: authors.sort((a, b) => b.karma - a.karma).slice(0, 15)
    },

    // 4. Comment activity (derived from post comment_counts)
    comment_activity: {
      total_comments_tracked: [
        ...(memoryPosts.posts || []),
        ...(openclawPosts.posts || []),
        ...(buildsPosts.posts || [])
      ].reduce((acc, p) => acc + (p.comment_count || 0), 0),
      most_discussed: [
        ...(memoryPosts.posts || []),
        ...(openclawPosts.posts || []),
        ...(buildsPosts.posts || [])
      ]
        .sort((a, b) => (b.comment_count || 0) - (a.comment_count || 0))
        .slice(0, 5)
        .map(p => ({
          title: p.title?.substring(0, 80),
          author: p.author?.name,
          comments: p.comment_count,
          submolt: p.submolt?.name
        }))
    },

    // 5. Submolt metadata
    submolts: {
      total_count: allSubmolts.submolts?.length || 0,
      total_posts_platform: allSubmolts.total_posts,
      total_comments_platform: allSubmolts.total_comments,
      target_submolts: [
        { name: 'memory', subscriber_count: 809 },
        { name: 'openclaw-explorers', subscriber_count: 890 },
        { name: 'builds', subscriber_count: 751 }
      ]
    },

    // 6. Daily summary
    summary: {
      total_posts_collected: (memoryPosts.posts?.length || 0) + (openclawPosts.posts?.length || 0) + (buildsPosts.posts?.length || 0),
      total_new_posts_global: newPosts.posts?.length || 0,
      unique_authors_tracked: authors.length,
      top_submolt_by_engagement: [
        { name: 'memory', engagement: calculateEngagement(memoryPosts.posts || []) },
        { name: 'openclaw-explorers', engagement: calculateEngagement(openclawPosts.posts || []) },
        { name: 'builds', engagement: calculateEngagement(buildsPosts.posts || []) }
      ].sort((a, b) => b.engagement - a.engagement)[0].name
    }
  };

  // Save snapshot
  fs.writeFileSync(outputFile, JSON.stringify(snapshot, null, 2));
  console.log(`âœ… Snapshot saved: ${outputFile}`);
  console.log(`ðŸ“Š Summary: ${snapshot.summary.total_posts_collected} posts, ${snapshot.authors.count} authors, ${snapshot.submolts.total_posts_platform} total platform posts`);
}

fetchData().catch(console.error);
SCRIPT
node /tmp/moltbook_daily_snapshot.js

