data:text/html,<!DOCTYPE html><html><head><style>body{margin:0;background:#0a0a0f;overflow:hidden}</style></head><body><canvas id='c'></canvas><script>
const c=document.getElementById('c'),x=c.getContext('2d'),w=c.width=1200,h=c.height=800;
const seed=Math.random();Math.random=()=>{const t=seed*15485863;return(seed*t)%1;};
const palette=['#ff006e','#8338ec','#3a86ff','#06ffa5','#ffbe0b','#fb5607'];
const nodes=[];for(let i=0;i<80;i++)nodes.push({x:Math.random()*w,y:Math.random()*h,vx:(Math.random()-.5)*.5,vy:(Math.random()-.5)*.5,r:Math.random()*3+1,color:palette[Math.floor(Math.random()*palette.length)]});
const gradients=[];for(let i=0;i<5;i++)gradients.push({x:Math.random()*w,y:Math.random()*h,r:Math.random()*300+200,color:palette[Math.floor(Math.random()*palette.length)]});
function draw(){x.fillStyle='#0a0a0f';x.fillRect(0,0,w,h);
for(let g of gradients){const r=x.createRadialGradient(g.x,g.y,0,g.x,g.y,g.r);r.addColorStop(0,g.color+'20');r.addColorStop(1,'transparent');x.fillStyle=r;x.fillRect(0,0,w,h);}
for(let i=0;i<nodes.length;i++){const n=nodes[i];n.x+=n.vx;n.y+=n.vy;if(n.x<0||n.x>w)n.vx*=-1;if(n.y<0||n.y>h)n.vy*=-1;
for(let j=i+1;j<nodes.length;j++){const m=nodes[j],d=Math.hypot(n.x-m.x,n.y-m.y);if(d<150){x.beginPath();x.strokeStyle=n.color+'40';x.lineWidth=1-d/150;x.moveTo(n.x,n.y);x.lineTo(m.x,m.y);x.stroke();}}}
for(let n of nodes){x.beginPath();x.fillStyle=n.color;x.arc(n.x,n.y,n.r,0,Math.PI*2);x.fill();x.beginPath();x.fillStyle='#fff';x.arc(n.x-n.r*.3,n.y-n.r*.3,n.r*.3,0,Math.PI*2);x.fill();}
requestAnimationFrame(draw);}draw();
</script></body></html>
