function showScaffold(filename) {
  const modal = document.getElementById('scaffold-modal');
  const content = document.getElementById('scaffold-content');
  const filenameEl = document.getElementById('scaffold-filename');

  filenameEl.textContent = filename;
  content.innerHTML = '<p style="color:#888;">Loading...</p>';
  modal.style.display = 'flex';

  fetch(scaffoldFiles[filename])
    .then(response => response.text())
    .then(text => {
      content.innerHTML = marked.parse(text);
    })
    .catch(err => {
      content.innerHTML = '<p style="color:#c00;">Failed to load file.</p>';
    });
}

function hideScaffold(event) {
  if (!event || event.target === document.getElementById('scaffold-modal')) {
    document.getElementById('scaffold-modal').style.display = 'none';
  }
}

